import React,{useEffect} from "react";
import AOS from 'aos';
import Isotope from 'isotope-layout'

//CSS FILES
import "./assets/vendor/aos/aos.css";
import "./assets/vendor/bootstrap/css/bootstrap.min.css";
import "./assets/vendor/bootstrap-icons/bootstrap-icons.css";
import "./assets/vendor/swiper/swiper-bundle.min.css";
import "./assets/css/style.css";

//IMAGE FILES
import pic1 from "./assets/img/img_1.jpg";


function App() {
  useEffect(() => {
    AOS.init({
      duration: 1000,
      easing: 'ease-in-out',
      once: true,
      mirror: false
    });
  }, [])

  useEffect(() =>{

  window.addEventListener('load', () => {
    let portfolioContainer = select('#portfolio-grid');
    if (portfolioContainer) {
      let portfolioIsotope = new Isotope(portfolioContainer, {
        itemSelector: '.item',
      });

      let portfolioFilters = select('#filters a', true);

      on('click', '#filters a', function(e) {
        e.preventDefault();
        portfolioFilters.forEach(function(el) {
          el.classList.remove('active');
        });
        this.classList.add('active');

        portfolioIsotope.arrange({
          filter: this.getAttribute('data-filter')
        });
        portfolioIsotope.on('arrangeComplete', function() {
          AOS.refresh()
        });
      }, true);
    }
  });

  }, [])

  return (
    <div>
       <section class="section site-portfolio">
        <div class="container">
          <div class="row mb-5 align-items-center">
            <div class="col-md-12 col-lg-6 mb-4 mb-lg-0" data-aos="fade-up">
              <h2>Hey, I'm Johan Stanworth</h2>
              <p class="mb-0">Freelance Creative &amp; Professional Graphics Designer</p>
            </div>
            <div class="col-md-12 col-lg-6 text-start text-lg-end" data-aos="fade-up" data-aos-delay="100">
              {/*TO DO: react router stuff for this?*/}
              <div id="filters" class="filters">
                <a href="#" data-filter="*" class="active">All</a>
                <a href="#" data-filter=".web">Web</a>
                <a href="#" data-filter=".design">Design</a>
                <a href="#" data-filter=".branding">Branding</a>
                <a href="#" data-filter=".photography">Photography</a>
              </div>
            </div>
          </div>

          {/*TO DO: make this into a component*/}
          <div id="portfolio-grid" class="row no-gutter" data-aos="fade-up" data-aos-delay="200">
            <div class="item web col-sm-6 col-md-4 col-lg-4 mb-4">
              <a href="work-single.html" class="item-wrap fancybox">
                <div class="work-info">
                  <h3>Boxed Water</h3>
                  <span>Web</span>
                </div>
                <img class="img-fluid" src={pic1}/>
              </a>
            </div>
          </div>
        </div>
      </section>

    <script src="./assets/js/main.js"></script>

    </div>
  );
}

export default App;
