import React,{useEffect} from "react";

import Navbar from "../Navbar.js";
import Footer from "../Footer.js";

import "../../assets/css/style.css";

//IMAGES
import header from "../../assets/img/logitech/header.svg";
import diary from "../../assets/img/logitech/diary.svg";
import ethnographic from "../../assets/img/logitech/ethnographic.svg";
import survey from "../../assets/img/logitech/survey.svg";
import secondary from "../../assets/img/logitech/secondary.svg";


function Logitech () {

    useEffect(() => {
        // üëáÔ∏è scroll to top on page load
        window.scrollTo({top: 0, left: 0, behavior: 'smooth'});
      }, []);

    return (
    <>

    <div class = "mt-5"></div>

        <div class ="fixed-top">
            <Navbar/>
        </div>


        <img src = {header}  class = "mt-5" data-aos="fade-up" style = {{width:"110%"}}></img>

            <section class="section">
            <div class="container">

                <div data-aos="fade-up" class = "align-items-center">
                    <h2 class = "logitech-title">How Might We Embrace All Play</h2>
                    <div class = "logitech border mb-3"></div>

                    <h3 class = "logitech-title">Problem Spaces</h3>
                    <div class = "row">

                        <div class = "col-6">
                            <ul class="list-line mb-5">
                                <li>Entry points to gaming communities and building permanence</li>
                                <li>Promoting realness in people and interactions</li>
                                <li>Forming community through in-game communication</li>
                                <li>Promoting safe, inclusive, and accessible spaces</li>
                                <li>Balancing between anonymity and connection</li>
                            </ul>   
                        </div>

                        <h4 class = "col-5">Ultimately, HMW encourage conversations that lead to <i>friendship</i> and <i>community</i> beyond the game?</h4>
                    </div>



                    <h2 class = "logitech-title">User Research</h2>
                    <div class = "logitech border mb-3"></div>
                    <div class = "row">
                        <img src = {secondary} class = "col-lg-3 col-md-3 col-sm-4"></img>
                        <img src = {survey} class = "col-lg-3 col-md-3 col-sm-6"></img>
                        <img src = {ethnographic} class = "col-lg-3 col-md-3 col-sm-6"></img>
                        <img src = {diary} class = "col-lg-3 col-md-3 col-sm-6"></img>
                    </div>
                </div>
            </div>



            </section>

        <Footer></Footer>    

    </>
    );
}

export default Logitech;
